import React, { Fragment, useState, useEffect } from "react";
import "./ImageLinkForm.css";

const ImageLinkForm = () => {
  const initialResp = [
    { qid: 1, resp: "" },
    { qid: 2, resp: "" },
    { qid: 3, resp: "" },
    { qid: 4, resp: "" },
    { qid: 5, resp: "" },
  ];
  const [questions, setQuestions] = useState();

  useEffect(() => {
    fetch("http://localhost:3000/form")
      .then((response) => response.json())
      .then((questions) => setQuestions(questions));
  }, []);

  const [responses, setResponses] = useState(initialResp);

  useEffect(() => {
    try {
      fetch("http://localhost:3000/showresponses/1")
        .then((data) => data.json())
        .then((responses) => {
          setResponses(responses);
          console.log(responses);
        });
    } catch (error) {
      alert("there was an error fetching your responses");
    }
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setValues({
      ...values,
      [name]: value,
    });
  };

  return (
    <div>
      <div className="center">
        <form className="form center pa4 br3 shadow-5">
          <label>{questions && questions[0].question}</label>
          <input
            className="f4 pa2 w-70 center"
            type="text"
            value={responses[0] ? responses[0].resp : ""}
            name="1"
            onChange={handleInputChange}
          />
          <label>{questions && questions[1].question}</label>
          <input
            className="f4 pa2 w-70 center"
            type="text"
            name="2"
            value={responses[1] ? responses[1].resp : ""}
            onChange={handleInputChange}
          />
          <label>{questions && questions[2].question}</label>
          <input
            className="f4 pa2 w-70 center"
            type="text"
            name="3"
            value={responses[2] ? responses[2].resp : ""}
            onChange={handleInputChange}
          />
          <label>{questions && questions[3].question}</label>
          <input
            className="f4 pa2 w-70 center"
            type="text"
            name="4"
            value={responses[3] ? responses[3].resp : ""}
            onChange={handleInputChange}
          />
          <label>{questions && questions[4].question}</label>
          <input
            className="f4 pa2 w-70 center"
            type="text"
            name="5"
            value={responses[4] ? responses[4].resp : ""}
            onChange={handleInputChange}
          />
          <button
            className="w-30 grow f4 link ph3 pv2 dib white bg-light-purple"
            // onClick={onButtonSubmit}
          >
            Detect
          </button>
        </form>
      </div>
    </div>
  );
};

export default ImageLinkForm;
