import React, { Fragment, useState, useEffect } from "react";
import "./ImageLinkForm.css";

const ImageLinkForm = () => {
  const [questions, setQuestions] = useState();

  useEffect(() => {
    fetch("http://localhost:3000/form")
      .then((response) => response.json())
      .then((questions) => setQuestions(questions));
    console.log(questions);
  }, []);

  const [responses, setResponses] = useState();

  useEffect(() => {
    try {
      fetch("http://localhost:3000/showresponses/1")
        .then((data) => data.json())
        .then((responses) => setResponses(responses));
      console.log(responses);
    } catch (error) {
      alert("there was an error fetching your responses");
    }
  }, [responses]);

  return (
    <div>
      <div className="center">
        <form className="form center pa4 br3 shadow-5">
          <label>{questions && questions[0].question}</label>
          <input
            className="f4 pa2 w-70 center"
            type="text"
            value={responses && responses[0].resp}
            name="one"
          />
          <label>{questions && questions[1].question}</label>
          <input
            className="f4 pa2 w-70 center"
            type="text"
            name="two"
            value={responses && responses[1].resp}
          />
          <label>{questions && questions[2].question}</label>
          <input
            className="f4 pa2 w-70 center"
            type="text"
            name="three"
            value={responses && responses[2].resp}
          />
          <label>{questions && questions[3].question}</label>
          <input
            className="f4 pa2 w-70 center"
            type="text"
            name="four"
            value={responses && responses[3].resp}
          />
          <label>{questions && questions[4].question}</label>
          <input
            className="f4 pa2 w-70 center"
            type="text"
            name="five"
            value={responses && responses[4].resp}
          />
          <button
            className="w-30 grow f4 link ph3 pv2 dib white bg-light-purple"
            // onClick={onButtonSubmit}
          >
            Detect
          </button>
        </form>
      </div>
    </div>
  );
};

export default ImageLinkForm;
